import React from "react";

const cardStyle = {
  width: '20vw',
  height: '20vh'
}

function Card({
  image,
  title,
  authors
}) {
  return <div className="card">

    <div className="card-body">
      <h5 class="card-title">{title}</h5>
      <p class="card-text">{(authors) ? authors.map((author) =>(author+" ")) : ""}</p>
    </div>
  </div>
}
export default Card;
