import React, {Component} from "react";
import $ from 'jquery';

// const cardStyle = {
//   width: '100px',
//   height: '100px'
// }
const cardStyle = {
  base:{
    backgroundImage: "url('https://i7.pngguru.com/preview/219/171/634/nappa-leather-goatskin-suede-leather.jpg')",
    marginTop: '1%',
    marginLeft: '.5%',
    marginRight: '.5%',
    marginBottom: '3%',
    height: '80vh',
    width: '15vw',
    float: 'left',
    border: '8px solid #17212C',
    transition: 'all 0.3s',
    ':hover': {
      transform: "translate(0px, -20px)"
    }
  }
}

const imgStyle = {
  base:{
    width: '100%',
    height: '44vh',
    objectFit: 'cover',
  }
}

const textStyle = {
  fontFamily: 'Trade Winds',
  color: 'gold'
}


class Card extends Component {
  constructor(props) {
    super(props);
    this.state = {

    };
  }
  saveBook = (title,authors,description,image,link) =>{
    $.post("/books",
    {
      title: title,
      authors: authors,
      description: description,
      image: image,
      link: link,
    },
    function(res, err){
      console.log("saved to db");
    });
  }

  flipCard = () => {
    if(document.getElementById("front").style.transform.includes("rotateY(180deg)")) {
        document.getElementById("front").style.transform = "rotateY(0deg)";
        document.getElementById("back").style.transform = "rotateY(180deg)";
        } else {
            document.getElementById("back").style.transform = "rotateY(0deg)";
            document.getElementById("front").style.transform = "rotateY(180deg)";

    }
  }
  render() {

    return <div className="card" style = {cardStyle.base}>
      <div id="front" className="card-front">
        <div className="card-body">
          <h5 style = {textStyle} class="card-title">{this.props.title}</h5>
          <p style = {textStyle} class="card-text">{(this.props.authors) ? this.props.authors.map((author) => (author + " ")) : ""}</p>
          <button type="submit" className="search-button btn btn-primary " onClick={()=>{this.saveBook(this.props.title,this.props.authors,this.props.description,this.props.image,this.props.link)}}>Save</button>
          <button type="submit" className="search-button btn btn-primary mr-3" onClick={()=>{window.location.href=this.props.link}}>View</button>

        </div>
        <div className="card-image">       
          <img style = {imgStyle.base} class="card-img-top" src={this.props.image} alt="CardImage"></img>


        </div>
      </div>
      <div className="card-back">
        <p>{this.props.description}</p>
      </div>
    </div>
  }
}
export default Card;
